import numpy as np
import matplotlib.pyplot as plt
import time
import math

## approximate pi by approximation using Monte Carlo (hit and miss of dart points) 
## and graph results
## where N is the number of "dart" points
def MonteCarloPi(N):
  start_time = time.time()
  x = np.random.uniform(low = -1, high = 1, size = (N,1))
  y = np.random.uniform(low = -1, high = 1, size = (N,1))

  hit = (x**2 + y**2) < 1        ## hit if point is inside circle
  approx = 4 * (np.sum(hit))/N   ## area = (pi)r^2, for a 1x1 circle = pi/4

  print('Approximation', round(approx, 6))
  end_time = time.time()
  print("Time: ", end_time - start_time)

MonteCarloPi(1000000)

