## function that simulates multiple takes of the MonteCarloPi function and averages 
## them together to find approximate value of pi to 6 decimal places
## where N is number of "dart" points per simulation (set to 10)
def MonteCarloPi6(N=10):
  start_time = time.time()
  sum_num = 0
  D = 1
  avg = -1
  while abs(math.pi - avg) > 0.0000001:
    x = np.random.uniform(low = -1, high = 1, size = (N,1))
    y = np.random.uniform(low = -1, high = 1, size = (N,1))
    hit = (x**2 + y**2) < 1
    sum_num = sum_num + (4 * (np.sum(hit))/N)
    D = D + 1
    avg = sum_num / (D-1)

  print ('Approximations of pi:', avg)
  print('Number of Simulations:', D-1)
  end_time = time.time()
  print ("Time: ", end_time - start_time)

